import React, { useState } from "react";
import DOMPurify from "dompurify";
import bcrypt from "bcryptjs";
import "./App.css"; // Archivo de estilos CSS

interface User {
  username: string;
  passwordHash: string;
}

const App = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [messages, setMessages] = useState<string[]>([]);
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [form, setForm] = useState({ username: "", password: "" });
  const [comment, setComment] = useState("");

  const handleRegister = async () => {
    if (users.find((u) => u.username === form.username)) {
      alert("Usuario ya registrado");
      return;
    }

    if (!form.username || !form.password) {
      alert("Debe ingresar un usuario y contraseña");
      return;
    }

    const passwordHash = await bcrypt.hash(form.password, 10);
    setUsers([...users, { username: form.username, passwordHash }]);
    alert("Usuario registrado con éxito");
    setForm({ username: "", password: "" });
  };

  const handleLogin = async () => {
    const user = users.find((u) => u.username === form.username);

    if (user && (await bcrypt.compare(form.password, user.passwordHash))) {
      setCurrentUser(user);
      alert("Login exitoso");
    } else {
      alert("Credenciales inválidas");
    }
  };

  const handleMessage = () => {
    const sanitizedComment = DOMPurify.sanitize(comment);
    setMessages([...messages, sanitizedComment]);
    setComment("");
  };

  return (
    <div className="container">
      <h2>Login / Registro</h2>
      <div className="form-container">
        <input
          type="text"
          placeholder="Usuario"
          value={form.username}
          onChange={(e) => setForm({ ...form, username: e.target.value })}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
        />
        <div className="button-group">
          <button onClick={handleRegister} className="btn">Registrarse</button>
          <button onClick={handleLogin} className="btn">Iniciar Sesión</button>
        </div>
      </div>

      {currentUser && (
        <div className="comment-section">
          <h2>Área de comentarios</h2>
          <textarea
            placeholder="Escribe un mensaje"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          />
          <button onClick={handleMessage} className="btn">Enviar</button>
          <div className="message-list">
            {messages.map((msg, index) => (
              <p key={index} className="message">{msg}</p>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
