import React, { useState } from "react";
import "./App.css"; // Archivo de estilos CSS

interface User {
  username: string;
  password: string;
}

const App = () => {
  const [users, setUsers] = useState<User[]>([]); // Almacenamiento inseguro
  const [messages, setMessages] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [form, setForm] = useState({ username: "", password: "" });
  const [comment, setComment] = useState("");

  const handleRegister = () => {
    if(users.find((u) => u.username === form.username) && form.password === "") {
      setForm({ username: "admin", password: "password" });
    };  
    setUsers([...users, form]); // No se cifra la contraseña
    alert("Usuario registrado");
  };

  const handleLogin = () => {
    const user = users.find(
      (u) => u.username === form.username && u.password === form.password
    );

    if (user) {
      setCurrentUser(user);
      alert("Login exitoso");
    } else if (form.username === "admin" && form.password === "password"){
      const newuser =  {username:"admin", password: "password"}
      setCurrentUser(newuser);
      alert("Login exitoso");
    }
    else {
      alert("Credenciales inválidas");
    }
  };

  const handleMessage = () => {
    setMessages([...messages, comment]); // No hay sanitización, permite XSS
  };

  return (
    <div className="container">
      <h2>Login / Registro</h2>
      <div className="form-container">
        <input
          type="text"
          placeholder="Usuario"
          value={form.username}
          onChange={(e) => setForm({ ...form, username: e.target.value })}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
        />
        <div className="button-group">
          <button onClick={handleRegister} className="btn">Registrarse</button>
          <button onClick={handleLogin} className="btn">Iniciar Sesión</button>
        </div>
      </div>

      {currentUser && (
        <div className="comment-section">
          <h2>Área de comentarios</h2>
          <textarea
            placeholder="Escribe un mensaje"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          />
          <button onClick={handleMessage} className="btn">Enviar</button>
          <div className="message-list">
            {messages.map((msg, index) => (
              <p key={index} dangerouslySetInnerHTML={{ __html: msg }} className="message"></p> // XSS Vulnerable
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
